// Analyze tasks data for weekly report
const tasksData = $input.all();
const now = new Date();
const oneWeekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);

// Initialize report data
const report = {
  totalTasks: 0,
  completedTasks: 0,
  inProgressTasks: 0,
  assignedTasks: 0,
  overdueTasks: 0,
  employeeStats: {},
  completedThisWeek: [],
  overdueList: []
};

// Process each task
for (const taskRow of tasksData) {
  const task = taskRow.json;

  if (!task.TaskID || task.TaskID === 'TaskID') continue; // Skip header row

  report.totalTasks++;

  // Count by status
  switch (task.Status) {
    case 'Completed':
      report.completedTasks++;

      // Check if completed this week
      if (task.CompletedAt) {
        const completedDate = new Date(task.CompletedAt);
        if (completedDate >= oneWeekAgo) {
          report.completedThisWeek.push({
            taskId: task.TaskID,
            description: task.Description,
            assignee: task.Assignee,
            completedAt: task.CompletedAt
          });
        }
      }
      break;
    case 'In Progress':
      report.inProgressTasks++;
      break;
    case 'Assigned':
      report.assignedTasks++;
      break;
  }

  // Check for overdue tasks
  if (task.Status !== 'Completed' && task.DueAt) {
    const dueDate = new Date(task.DueAt);
    if (dueDate < now) {
      report.overdueTasks++;
      report.overdueList.push({
        taskId: task.TaskID,
        description: task.Description,
        assignee: task.Assignee,
        dueAt: task.DueAt,
        daysPastDue: Math.floor((now - dueDate) / (24 * 60 * 60 * 1000))
      });
    }
  }

  // Employee statistics
  const assignee = task.Assignee || 'Unassigned';
  if (!report.employeeStats[assignee]) {
    report.employeeStats[assignee] = {
      totalAssigned: 0,
      completed: 0,
      inProgress: 0,
      overdue: 0
    };
  }

  report.employeeStats[assignee].totalAssigned++;

  if (task.Status === 'Completed') {
    report.employeeStats[assignee].completed++;
  } else if (task.Status === 'In Progress') {
    report.employeeStats[assignee].inProgress++;
  }

  if (task.Status !== 'Completed' && task.DueAt && new Date(task.DueAt) < now) {
    report.employeeStats[assignee].overdue++;
  }
}

// Calculate completion rate
report.completionRate = report.totalTasks > 0 ? Math.round((report.completedTasks / report.totalTasks) * 100) : 0;

// Generate report date
report.reportDate = now.toLocaleDateString('en-US', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

report.weekRange = `${oneWeekAgo.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} - ${now.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`;

return {
  json: report
};

