// Extract and validate lead data
const leadData = $input.first().json;

// Calculate lead score based on available information
let leadScore = 0;

// Scoring criteria
if (leadData.email) leadScore += 20;
if (leadData.phone) leadScore += 20;
if (leadData.company) leadScore += 15;
if (leadData.job_title) leadScore += 15;
if (leadData.budget && parseFloat(leadData.budget) > 1000) leadScore += 20;
if (leadData.timeline && ['immediate', 'this_month'].includes(leadData.timeline)) leadScore += 10;

// Source-based scoring
const sourceScoring = {
  'website_form': 15,
  'social_media': 10,
  'referral': 25,
  'advertisement': 12,
  'cold_outreach': 5
};
leadScore += sourceScoring[leadData.source] || 5;

// Determine qualification status
let qualificationStatus = 'new';
if (leadScore >= 70) qualificationStatus = 'qualified';
else if (leadScore >= 40) qualificationStatus = 'warm';
else if (leadScore < 25) qualificationStatus = 'cold';

return {
  name: leadData.name || '',
  email: leadData.email || '',
  phone: leadData.phone || '',
  company: leadData.company || '',
  job_title: leadData.job_title || '',
  budget: leadData.budget || '',
  timeline: leadData.timeline || '',
  source: leadData.source || 'unknown',
  message: leadData.message || '',
  lead_score: leadScore,
  qualification_status: qualificationStatus,
  utm_source: leadData.utm_source || '',
  utm_campaign: leadData.utm_campaign || '',
  utm_medium: leadData.utm_medium || '',
  page_url: leadData.page_url || '',
  created_at: new Date().toISOString(),
  metadata: {
    user_agent: leadData.user_agent || '',
    ip_address: leadData.ip_address || '',
    referrer: leadData.referrer || '',
    form_id: leadData.form_id || '',
    additional_fields: leadData.additional_fields || {}
  }
};

