// Create task for sales team for qualified leads
const leadData = $('process-lead-data').first().json;
const leadRecord = $('save-lead').first().json;

if (leadData.qualification_status === 'qualified') {
  return {
    type: 'qualified_lead_followup',
    priority: 'high',
    title: `Follow up with qualified lead: ${leadData.name}`,
    description: `New qualified lead (Score: ${leadData.lead_score}) from ${leadData.source}. Company: ${leadData.company}. Budget: ${leadData.budget}. Timeline: ${leadData.timeline}.`,
    lead_id: leadRecord.id,
    assigned_to: 'sales_team',
    due_date: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(), // 24 hours from now
    metadata: {
      lead_data: leadData,
      contact_attempts: 0,
      preferred_contact_method: leadData.phone ? 'phone' : 'email'
    }
  };
}

return null;

